component.schema:
{
 "description": "AerospikeBackup is the Schema for the aerospikebackup API",
 "properties": {
  "spec": {
   "description": "AerospikeBackupSpec defines the desired state of AerospikeBackup for a given AerospikeCluster",
   "properties": {
    "backupService": {
     "description": "BackupService is the backup service reference i.e. name and namespace.\nIt is used to communicate to the backup service to trigger backups. This field is immutable",
     "properties": {
      "name": {
       "description": "Backup service name",
       "type": "string"
      },
      "namespace": {
       "description": "Backup service namespace",
       "type": "string"
      }
     },
     "required": [
      "name",
      "namespace"
     ],
     "type": "object"
    },
    "config": {
     "description": "Config is the free form configuration for the backup in YAML format.\nThis config is used to trigger backups. It includes: aerospike-cluster, backup-routines.",
     "format": "textarea",
     "type": "string"
    },
    "onDemandBackups": {
     "description": "OnDemandBackups is the configuration for on-demand backups.",
     "items": {
      "properties": {
       "delay": {
        "description": "Delay is the interval before starting the on-demand backup.",
        "type": "string"
       },
       "id": {
        "description": "ID is the unique identifier for the on-demand backup.",
        "minLength": 1,
        "type": "string"
       },
       "routineName": {
        "description": "RoutineName is the routine name used to trigger on-demand backup.",
        "type": "string"
       }
      },
      "required": [
       "id",
       "routineName"
      ],
      "type": "object"
     },
     "maxItems": 1,
     "type": "array"
    }
   },
   "required": [
    "backupService",
    "config"
   ],
   "type": "object"
  }
 },
 "title": "Aerospike Backup",
 "type": "object"
}
