component.schema:
{
 "description": "AerospikeRestore is the Schema for the aerospikerestores API",
 "properties": {
  "spec": {
   "description": "AerospikeRestoreSpec defines the desired state of AerospikeRestore",
   "properties": {
    "backupService": {
     "description": "BackupService is the backup service reference i.e. name and namespace.\nIt is used to communicate to the backup service to trigger restores. This field is immutable",
     "properties": {
      "name": {
       "description": "Backup service name",
       "type": "string"
      },
      "namespace": {
       "description": "Backup service namespace",
       "type": "string"
      }
     },
     "required": [
      "name",
      "namespace"
     ],
     "type": "object"
    },
    "config": {
     "description": "Config is the free form configuration for the restore in YAML format.\nThis config is used to trigger restores. It includes: destination, policy, source, secret-agent, time and routine.",
     "format": "textarea",
     "type": "string"
    },
    "pollingPeriod": {
     "description": "PollingPeriod is the polling period for restore operation status.\nIt is used to poll the restore service to fetch restore operation status.\nDefault is 60 seconds.",
     "type": "string"
    },
    "type": {
     "description": "Type is the type of restore. It can of type Full, Incremental, and Timestamp.\nBased on the restore type, the relevant restore config should be given.",
     "enum": [
      "Full",
      "Incremental",
      "Timestamp"
     ],
     "type": "string"
    }
   },
   "required": [
    "backupService",
    "config",
    "type"
   ],
   "type": "object"
  }
 },
 "title": "Aerospike Restore",
 "type": "object"
}
